# Initialize Flask app
app = Flask(__name__)

app.config['SQLALCHEMY_DATABASE_URI'] = os.environ.get('DATABASE_URL')
db = SQLAlchemy(app)

class SkinProds(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    name = db.Column(db.String(200), unique=True, nullable = False)
    brand_name = db.Column(db.String(200), nullable = False)
    product_type = db.Column(db.String(200), nullable=True)
    skin_type = db.Column(db.String(200), default="Normal, Dry, Oily, Combo, Sensitive")
    ingredients = db.Column(db.String(1600),  nullable=False)
  
    
    def as_dict(self):
       return {c.name: getattr(self, c.name) for c in self.__table__.columns}

    @property
    def serialize(self):
    #return serialized object
        return {
            'id' : self.id,
            'name' : self.name,
            'brand_name' : self.brand_name,
            'product_type' : self.product_type,
            'skin_type' : self.skin_type,
            'ingredients' : self.ingredients,
        }
  
    def __init__(self, name, brand_name, product_type, ingredients, skin_type="Normal, Dry, Oily, Combo, Sensitive"):
        self.name = name
        self.brand_name = brand_name
        self.product_type = product_type
        self.skin_type = skin_type
        self.ingredients = ingredients


