# queries SkinProds database after building query filter using params passed if HTTP "GET" request 
@app.route('/api/v2/resources/skincareproducts', methods=['GET'])
def api_filter():
    query_parameters = request.args
    for key in query_parameters:
        print (f'argument {key} and value {query_parameters.get(key)}', flush=True)

    # handle filter of ingredient(s) specified here-- gets parameters values from url
    id = query_parameters.get('id')
    name = query_parameters.get('name')
    #specify >1 brand name
    brand_name_list = query_parameters.get('brand')
    product_type = query_parameters.get('productType')
    # specify >1 skin type (ex. "Dry" & "Sensitive")--> check if "getlist" works
    skin_types_list = query_parameters.get('skinTypes')
    # how to handle ingredients(be able to have user specify >1 ingredient at a time)
    ingredients_list = query_parameters.get('ingredients')

    query = db.session.query(SkinProds)
    if id:
        query = query.filter(SkinProds.id==id) #search for product by id

    if name:
        query = query.filter(SkinProds.name==name) #search for exact product name
        #query = query.filter(SkinProds.name.ilike(f'%{str(name)}%'))

    if brand_name_list:
        #if multiple brand specified:
        try:
            search_brands = brand_name_list.split(",")
            brand_list = [br.strip() for br in search_brands]
            print (f'brand list{brand_list}', flush=True)
            filter_list = [SkinProds.brand_name.ilike(f'%{str(brand)}%') for brand in brand_list] #ilike allows user to enter brand name without exact capitalization
            print (f'filter list{filter_list}', flush=True)
            query = query.filter(or_(*filter_list)) #gets results when any brand name specified is recognized in database
        except:
            #if only one brand specified:
            brand = brand_name_list
            query = query.filter(SkinProds.brand_name.ilike("%"+str(brand)+"%")) 
   
    if product_type:
        query = query.filter(SkinProds.product_type.ilike("%"+str(product_type)+"%")) #ilike for nonspecific capitalization

    if skin_types_list:
        #if multiple skin types specified:
        try:
            search_skin_types = skin_types_list.split(",")
            for type in search_skin_types:
                query = query.filter(SkinProds.skin_type.ilike("%"+str(type)+"%")) #gets results when all skin types specified are matched
        except:
            #if only one skin type specified:
            type = skin_types_list
            query = query.filter(SkinProds.skin_type.ilike("%"+str(type)+"%"))

    if ingredients_list:
        #handles entry of multiple ingredients that are unwanted
        try:
            search_ingredients = ingredients_list.split(",")
            refined_search_ingredients = [ing.strip() for ing in search_ingredients]
            filter_list = [~SkinProds.ingredients.ilike(f'%{str(ingredient)}%') for ingredient in refined_search_ingredients]
            query = query.filter(and_(*filter_list))
        except:
            #if only one unwanted ingredient specified
            ingredient = ingredients_list
            query = query.filter(~SkinProds.ingredients.ilike("%"+str(ingredient)+"%"))

    # now the built query is run
    results = query.all()
    response_skinprods = []
    for skinprod in results:
        del skinprod.__dict__['_sa_instance_state']
        response_skinprods.append(skinprod.__dict__)
   
    return jsonify(response_skinprods) #returns jsonified API response


@app.route('/api/v2/resources/skincareproducts', methods=['POST'])
def add_skinprod():
    # Receives data in JSON format for a HTTP "POST" request
    if not request.is_json: #error message if data not passed in JSON format
        return "<p>The data you wish to add is not of type JSON<\p>"
    
    new_sp = request.get_json()
    db.session.add(SkinProds(new_sp['name'], new_sp['brand_name'], new_sp['product_type'], new_sp['ingredients'], new_sp['skin_type']))
    db.session.commit()
    
    return {"message": f"Skincare product {new_sp['name']} successfully added to SkinProds database"}

@app.route('/api/v2/resources/skincareproducts/<sp_id>', methods=['GET', 'PUT', 'DELETE'])
def handle_skinprod(sp_id):
    skinprod = SkinProds.query.get_or_404(sp_id)

    if request.method == 'GET': #gets product details by id without specifying query param "id"
       
        return jsonify(skinprod.serialize)

    elif request.method == 'PUT':
        updated_sp_data = request.get_json() #gets new information for existing product (in JSON type)
        # uses information to update each attribute of skin product
        skinprod.name = updated_sp_data['name']
        skinprod.brand_name = updated_sp_data['brand_name']
        skinprod.product_type = updated_sp_data['product_type']
        skinprod.skin_type = updated_sp_data['skin_type']
        skinprod.ingredients = updated_sp_data['ingredients']
        db.session.add(skinprod) #replaces old product with new, updated product
        db.session.commit() #commits changes
        return {"message": f"Skincare product {skinprod.name} successfully updated"}

    elif request.method == 'DELETE': # deletes specified product using product id info
        db.session.delete(skinprod)
        db.session.commit()
        return {"message": f"Skincare product {skinprod.name} successfully deleted."}

